
public class BooleanOperators {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		/*
		 * Boolean Operators
		 * == equality
		 * > greater than
		 * < less than
		 * >= greater than or equal to
		 * <= less than or equal to 
		 * 
		 *  && AND, if two boolean expressions evaluate to true, the whole expression is true
		 *  || OR, if either the boolean expression evaluates to true, then the whole expression is true
		 */
		
		double moneyInPocket = 10.50;
		double temperature = 108.08;
		boolean isHotOutside = temperature >= 100;
		boolean willBuyIeCream = isHotOutside && moneyInPocket > 5;
		
		System.out.println();
		
		

		
		
		
		
	}

}
